import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Button from "react-bootstrap/Button";
import { NavLink } from "react-router-dom";
import {
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardText,
  MDBIcon,
  MDBTypography,
  MDBBtn,
  MDBTableBody,
  MDBTableHead,
  MDBTable,
} from "mdb-react-ui-kit";
import { useNavigate } from "react-router-dom";
export default function Details() {
  const { id } = useParams();
  const [status, setStatus] = useState({});
  const [customer, setCustomer] = useState({});
  const [loading, setLoading] = useState(true);
  const [data, setdata] = useState({});
  const history = useNavigate();
  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/loan/${id}`);
      setStatus(response.data);

      const customerNo = response.data.customerId;

      const responseCustomer = await axios.get(
        `http://localhost:3001/customer/${customerNo}`
      );

      setCustomer(responseCustomer.data);

      const responsedata = await axios.get(
        `http://localhost:3002/dailydebit/${id}`
      );
      setdata(responsedata.data);

      setLoading(false);
    } catch (error) {
      console.error("Error:", error);
    }
  };
  const handleDownload = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/pdf/${status.customerId}/${id}`);
      console.log(`${status.customerId}_${id}`);

    //   const fileName = `${status.customerId}_${id}`;
       const blob = new Blob([response.data], { type: 'application/octet-stream' });
       const url = window.URL.createObjectURL(blob);
