import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Button from "react-bootstrap/Button";
import { NavLink } from "react-router-dom";
import {
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardText,
  MDBIcon,
  MDBTypography,
  MDBBtn,
  MDBTableBody,
  MDBTableHead,
  MDBTable,
  MDBInput,
  MDBTextArea,
  MDBCheckbox,
  MDBCardTitle,
} from "mdb-react-ui-kit";
import Card from "react-bootstrap/Card";
export default function Email() {
  const { id } = useParams();
  const { newId } = useParams();
  const [status, setStatus] = useState({});
  const [customer, setCustomer] = useState({});
  const [loading, setLoading] = useState(true);
  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/email/${id}/${newId}`);
      setStatus(response.data);

      const responseCustomer = await axios.get(
        `http://localhost:3001/customer/${id}`
      );

      setCustomer(responseCustomer.data);

      setLoading(false);
    } catch (error) {
      console.error("Error:", error);
    }
  };
  useEffect(() => {
    fetchData();
  }, [id]);
  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <section style={{ backgroundColor: "#eee" }}>
      <MDBContainer className="py-5">
        <MDBRow>
          <MDBCol lg="6">
            <MDBCard className="mb-2">
              <MDBCardBody className="text-center">
                <form>
                  <div className="grey-text">
                    <p className="h3 text-center mb-4">Email</p>
                    <h6 className="text-right"> Customer ID : {id}</h6>
                    <h6 className="text-right"> Loan ID : {newId}</h6>
                    <div className="input-group mb-3">
                      <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon">
                          <i className="material-icons">email</i>
                        </span>
                      </div>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Email"
                        aria-label="Email"
                        aria-describedby="basic-addon"
                      />
                    </div>

                    <div className="input-group">
                      <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon">
                          <div>
                            <i className="material-icons">edit</i>
                          </div>
                        </span>
                      </div>
                      <textarea
                        className="form-control"
                        placeholder="Message"
                        aria-label="Message"
                        id="exampleFormControlTextarea1"
                        rows="5"
                      ></textarea>
                    </div>

                    <div className="text-center mt-4">
                      <MDBBtn outline color="secondary">
                        Send
                      </MDBBtn>
                    </div>
                  </div>
                </form>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>

          <MDBCol lg="6">
      <MDBCard className="mb-4">
        <MDBCardBody>
          <p className="h3 text-center mb-4">Email History</p>
          {status.length > 0 ? (
            status.map((el, i) => (
              <Card key={i}>
                <Card.Header>{el.date}</Card.Header>
                <Card.Body>
                  <blockquote className="blockquote mb-0">
                    <p>{el.email}</p>
                    <footer className="blockquote-footer">
                      {el.sender}
                      <cite title={el.sourceTitle}>{el.sourceTitle}</cite>
                    </footer>
                  </blockquote>
                </Card.Body>
              </Card>
            ))
          ) : (
            !loading && <p>No email history available.</p>
          )}
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
