import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import InputGroup from "react-bootstrap/InputGroup";

export default function Register() {
  const [customerId, setcustomerId] = useState("");
  console.log(customerId);

  const [amount, setAmount] = useState("");
  console.log(amount);

  const [duration, setduration] = useState("");
  console.log(duration);

  const [number, setNo] = useState("");
  console.log(duration);

  const history = useNavigate();

  const setdata = (e) => {
    const { value } = e.target;
    setcustomerId(value);
  };

  const setnumber = (e) => {
    const { value } = e.target;
    setAmount(value);
  };
  const setDurationNumber = (e) => {
    const { value } = e.target;
    setduration(value);
  };

  const setTrackingNumber = (e) => {
    const { value } = e.target;
    setNo(value);
  };

  // add user data

  const addLoanData = async (e) => {
    e.preventDefault();

    const loan = {
      customerId: customerId,
      amount: amount,
      trackingNumber: number,
      duration: duration,
    };

    const config = {
      Headers: {
        "Content-type": "application/json",
      },
    };

    const res = await axios.post("http://localhost:3001/loan", loan, config);
    console.log(res);
    if (res.data.status === 401 || !res.data) {
      console.log("error");
    } else {
      history("/");
    }
  };

  return (
    <div className="container mt-3">
      <h2>Enter Loan Details</h2>

      <Form>
        <Form.Group className="mb-3">
          <Form.Label>CustomerID</Form.Label>

          <select className="form-controller">
            <option>Choose CustomerID</option>
          </select>
          <Form.Control
            type="text"
            name="customerId"
            onChange={setdata}
            placeholder=""
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Amount </Form.Label>
          <InputGroup className="mb-3">
            <InputGroup.Text>$</InputGroup.Text>
            <Form.Control
              type="number"
              aria-label="Amount (to the nearest dollar)"
              onChange={setnumber}
              name="amount"
              placeholder="1000"
            />
            <InputGroup.Text>.00</InputGroup.Text>
          </InputGroup>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Duration </Form.Label>
          <InputGroup className="mb-3">
            <InputGroup.Text>Dates</InputGroup.Text>
            <Form.Control
              type="number"
              onChange={setDurationNumber}
              name="duration"
              placeholder="180"
            />
          </InputGroup>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>TrackingNumber</Form.Label>
          <Form.Control
            type="text"
            onChange={setTrackingNumber}
            name="trackingNumber"
            placeholder=""
          />
        </Form.Group>

        <Button variant="dark" type="submit" onClick={addLoanData}>
          Submit
        </Button>
      </Form>
    </div>
  );
}
