import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
const Details = () => {
  const [customer, setCustomer] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/loan/${id}`);
        setCustomer(response.data);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchData();
  }, []);

  console.log(customer);

  return (
    <div className="d-flex w-100 vh-100 justify-content-center align-items-center">
        <h2 className="text-center mt-2">Update Loan Status</h2>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Loan ID</Form.Label>
          <br></br>
          <Form.Text className="text-muted">{customer.loanId}</Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Loan Status Update</Form.Label>
          <Form.Select aria-label="Default select example">
            <option>Choose ...</option>
            <option value="OUTSTANDING">OUTSTANDING</option>
            <option value="CLOSED">CLOSED</option>
          </Form.Select>
        </Form.Group>

        <Button variant="success" type="submit">
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default Details;
