import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

function UpdateLoanStatus({ show, onHide, onUpdate }) {
       const [updatedData, setUpdatedData] = useState({}); // State for the updated data

        const handleInputChange = (e) => {
          const { name, value } = e.target;
          setUpdatedData({ ...updatedData, [name]: value });
        };

        const handleUpdate = () => {
          onUpdate(updatedData); // Pass the updated data to the parent component
          onHide(); // Close the modal
        };
  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Update Data</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          {/* Input fields for updating data */}
          <Form.Group>
            <Form.Label>Field 1</Form.Label>
            <Form.Control
              type="text"
              name="field1"
              value={updatedData.field1 || ''}
              onChange={handleInputChange}
            />
          </Form.Group>

          {/* Add more input fields as needed */}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Close
        </Button>
        <Button variant="primary" onClick={handleUpdate}>
          Update
        </Button>
      </Modal.Footer>
    </Modal>
  )
}

export default UpdateLoanStatus
