import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import InputGroup from "react-bootstrap/InputGroup";

export default function Register() {
  const [customerId, setcustomerId] = useState("");
  const [amount, setAmount] = useState("");
  const [duration, setDuration] = useState("");
  const [number, setNo] = useState("");
  const [customerList, setCustomerList] = useState([]);

  const history = useNavigate();

  const setdata = (e) => {
    const { value } = e.target;
    setcustomerId(value);
  };

  const setnumber = (e) => {
    const { value } = e.target;
    setAmount(value);
  };
  const setDurationNumber = (e) => {
    const { value } = e.target;
    setDuration(value);
  };

  const setTrackingNumber = (e) => {
    const { value } = e.target;
    setNo(value);
  };

  const getCustomerList = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/customer`, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      // Update the state with the paginated data
      setCustomerList(response.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getCustomerList();
  }, []); // Re-fetch data when the component mounts

  const addLoanData = async (e) => {
    e.preventDefault();

    const loan = {
      customerId: customerId,
      amount: amount,
      trackingNumber: number,
      duration: duration,
    };

    const config = {
      Headers: {
        "Content-type": "application/json",
      },
    };

    const res = await axios.post("http://localhost:3001/loan", loan, config);
    console.log(res);
    if (res.data.status === 401 || !res.data) {
      console.log("error");
    } else {
      history("/");
    }
  };

  return (
    <div className="container mt-3">
      <h2>Enter Loan Details</h2>

      <Form>
        <Form.Group className="mb-3">
          <Form.Label>CustomerID</Form.Label>
          <Form.Select
            aria-label="Default select example"
            onChange={setdata}
            value={customerId}
          >
            <option value="">Choose CustomerID</option>
            {customerList.map((customer) => (
              <option value={customer.id} key={customer.id}>
                {customer.name}
              </option>
            ))}
          </Form.Select>
        </Form.Group>

        {/* The rest of your form input fields... */}

        <Button variant="dark" type="submit" onClick={addLoanData}>
          Submit
        </Button>
      </Form>
    </div>
  );
}
