import React, { useEffect, useState } from "react";
import Button from "react-bootstrap/Button";
import { NavLink } from "react-router-dom";
import axios from "axios";
import Table from "react-bootstrap/Table";

export default function Customer() {
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1); // Current page

  const getUserData = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3001/customer?page=${page}`,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      // Update the state with the paginated data
      setData(response.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getUserData();
  }, [page]); // Re-fetch data when page or perPage changes

  const goToPage = (newPage) => {
    setPage(newPage);
  };

  return (
    <div className="container mt-2">
      <h2 className="text-center mt-2">Loan Details</h2>
      <div className="text-end">
        <Button variant="dark">
          <NavLink to="/register" className="text-decoration-none text-light">
            Add Loan
          </NavLink>
        </Button>
      </div>
      <div className="row d-flex align-items-center mt-5">
        <Table striped>
          <thead>
            <tr>
              <th>id</th>
              <th>Name</th>
              <th>Loan Amount</th>
              <th>Duration</th>
              <th>Start Day</th>
              <th>More ...</th>
            </tr>
          </thead>
          <tbody>
            {data.map((el, i) => (
              <tr key={i}>
                <td>{el.customerId}</td>
                <td>{el.name}</td>
                <td>$ {el.email}</td>
                <td>{el.duration}</td>
                <td>{new Date(el.date).toLocaleDateString()}</td>
                <td>
                  <Button variant="dark">
                    <NavLink
                      to={`/details/${el._id}`}
                      className="text-decoration-none text-light"
                    >
                      Details
                    </NavLink>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
      <div className="text-center">
        <Button variant="light" onClick={() => goToPage(page - 1)}>
          {page}
        </Button>{" "}
        <Button variant="light" onClick={() => goToPage(page + 1)}>
        {page+1}
        </Button>
      </div>
    </div>
  );
}
