import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Table from 'react-bootstrap/Table';
import ListGroup from 'react-bootstrap/ListGroup';
import Accordion from 'react-bootstrap/Accordion';
import {Form, Button} from 'react-bootstrap/Form';
const Details = () => {
  const [customer, setCustomer] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {

        const response = await axios.get(`http://localhost:3001/loan/${id}`);
        setCustomer(response.data);

      } catch (error) {
        console.error("Error:", error);

      }
    };

    fetchData();
  }, []);


  console.log(customer);

  return (
    <div className="container mt-2">
<Form>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Loan ID</Form.Label>
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control type="password" placeholder="Password" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
<Accordion defaultActiveKey={['0']} alwaysOpen>
      <Accordion.Item eventKey="0">
        <Accordion.Header>Customer Details</Accordion.Header>
        <Accordion.Body>
        <ListGroup>
      <ListGroup.Item>Loan ID: {customer.loanId}</ListGroup.Item>

    </ListGroup>


        </Accordion.Body>
        <Form.Select aria-label="Default select example">
      <option>Open this select menu</option>
      <option value="1">One</option>
      <option value="2">Two</option>
      <option value="3">Three</option>
    </Form.Select>

      </Accordion.Item>
    </Accordion>
        </div>


      ) 

              }


export default Details;
