import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Button from "react-bootstrap/Button";
import { NavLink } from "react-router-dom";
import {
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardText,
  MDBIcon,
  MDBTypography,
  MDBBtn,
  MDBTableBody,
  MDBTableHead,
  MDBTable,
  MDBInput,
  MDBTextArea,
  MDBCheckbox,
  MDBCardTitle,
} from "mdb-react-ui-kit";
import Card from 'react-bootstrap/Card';
export default function Email() {
  const { id } = useParams();
  const [status, setStatus] = useState({});
  const [customer, setCustomer] = useState({});
  const [loading, setLoading] = useState(true);
  const [data, setdata] = useState({});
  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/loan/${id}`);
      setStatus(response.data);

      const customerNo = response.data.customerId;

      const responseCustomer = await axios.get(
        `http://localhost:3001/customer/${customerNo}`
      );

      setCustomer(responseCustomer.data);

      const responsedata = await axios.get(
        `http://localhost:3002/dailydebit/${id}`
      );
      setdata(responsedata.data);

      setLoading(false);
    } catch (error) {
      console.error("Error:", error);
    }
  };
  useEffect(() => {
    fetchData();
  }, [id]);
  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <section style={{ backgroundColor: "#eee" }}>
      <MDBContainer className="py-5">
        <MDBRow>
          <MDBCol lg="6">
            <MDBCard className="mb-2">
              <MDBCardBody className="text-center">
              <form>
  <div className="grey-text">
    <p className="h5 text-center mb-4">Email</p>
    <div className="input-group mb-3">
      <div className="input-group-prepend">
        <span className="input-group-text" id="basic-addon">
          <i className="material-icons">email</i>
        </span>
      </div>
      <input
        type="text"
        className="form-control"
        placeholder="Email"
        aria-label="Email"
        aria-describedby="basic-addon"
      />
    </div>

    <div className="input-group">
      <div className="input-group-prepend">
        <span className="input-group-text" id="basic-addon">
          <div>
            <i className="material-icons">edit</i>
          </div>
        </span>
      </div>
      <textarea
        className="form-control"
        placeholder="Message"
        aria-label="Message"
        id="exampleFormControlTextarea1"
        rows="5"
      ></textarea>
    </div>

    <div className="text-center mt-4">
      <MDBBtn outline color="secondary">
        Send
      </MDBBtn>
    </div>
  </div>
</form>

              </MDBCardBody>
            </MDBCard>
          </MDBCol>

          <MDBCol lg="6">
            <MDBCard className="mb-4">
              <MDBCardBody>

              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
