import React, { useRef, useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Alert from "react-bootstrap/Alert";
import Modal from "react-bootstrap/Modal";

export default function Register() {
  const formRef = useRef(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [fname, setFName] = useState("");
  const [amount, setAmount] = useState("");
  const [show, setShow] = useState(false); // Add state for the modal

  const history = useNavigate();

  const setdata = (e) => {
    const { value } = e.target;
    setFName(value);
  };

  const setnumber = (e) => {
    const { value } = e.target;
    setAmount(value);
  };

  // add user data
  const addLoanData = async (e) => {
    e.preventDefault();

    const loan = {
      fname: fname,
      amount: amount,
    };

    const config = {
      headers: {
        "Content-type": "application/json",
      },
    };

    try {
      const res = await axios.post("http://localhost:3001/customer", loan, config);
      console.log(res);
      if (res.data.status === 401 || !res.data) {
        setErrorMessage("Error occurred.");
      } else {
        history("/");
      }
    } catch (error) {
      setErrorMessage("Error occurred.");
    }
  };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton closeVariant="white" className="text-bg-primary">
          <Modal.Title>Add New User</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={addLoanData} ref={formRef}>
            <Form.Group className="mb-3">
              <Form.Label className="fw-bold"> Full Name</Form.Label>
              <Form.Control
                required
                placeholder="Enter Full Name"
                type="text"
                name="name"
                onChange={setdata}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label className="fw-bold"> Email</Form.Label>
              <Form.Control
                required
                placeholder="Enter Email Address"
                type="email"
                name="email"
                onChange={setnumber}
              />
            </Form.Group>

            {errorMessage && (
              <Alert variant="danger" dismissible onClose={() => setErrorMessage("")}>
                {errorMessage}
              </Alert>
            )}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="outline-secondary"
            className="me-auto"
            onClick={(e) => {
              formRef.current.reset();
              setErrorMessage("");
            }}
          >
            Reset fields
          </Button>
          <Button variant="danger" onClick={handleClose}>
            Cancel
          </Button>
          <Button variant="success" type="submit" onClick={handleShow}>
            Add User
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
